  };

  const handleSizeChange = (e) => {
    setSize(e.target.value);
  };

  const handleLeftOrRightChange = (e) => {
    setLeftOrRight(e.target.value);
  };  

  const handleBottomPointChange = (e) => {
    setTopOrBottom(e.target.value);
  };

  return (
    <div className="MainForm_ym">
      <form className="max-w-xl mx-auto bg-slate-800	ymForm">
        {/* Form input fields */}
      </form>

      {/* WhatsApp button */}
      <div className="flex items-start mb-5 ResultCode">
        <a
          href={`https://api.whatsapp.com/send?phone=${number}&text=${text}`}
          className={`whatsapp-button`}
          id={id}
          style={{
            position: "fixed",
            zIndex: "1000",
            display: "block",
            width: `${Size}px`,
            borderRadius: "50%",
            backgroundColor: "#25d366",
            color: "#fff",
            textAlign: "center",
            fontSize: "30px",
            boxShadow: "2px 2px 3px #999",
            textDecoration: "none",
            bottom: `${TopOrBottom}px`, // Updated to use TopOrBottom
            right: `${LeftOrRight}px`, // Updated to use LeftOrRight
          }}
          target="_blank"
        >
          <img src={WhatsAppLogo} alt="WhatsApp " />
        </a>
      </div>

      {/* Result code display */}
      <div className="result background">
        <pre><code className="html ResultCode">"asdasdasd"</code></pre>
      </div>
    </div>
  );
}

export default App;
